_G2PW_PREDICTORS: Dict[str, BertPolyPredict] = {}
_G2PW_LOCK = threading.Lock()


def _normalize_device(device: Optional[Union[str, int]]) -> str:
    if device is None:
        env_value = os.getenv("F5TTS_G2PW_DEVICE")
        if env_value:
            return _normalize_device(env_value)
        return "auto"
    if isinstance(device, int):
        return f"cuda:{device}"
    device_str = str(device).strip().lower()
    if device_str in {"", "auto"}:
        return "auto"
    if device_str == "cpu":
        return "cpu"
    if device_str.isdigit():
        return f"cuda:{device_str}"
    if device_str.startswith("cuda:"):
        suffix = device_str.split(":", 1)[1]
        if suffix.isdigit():
            return f"cuda:{suffix}"
        raise ValueError(f"Invalid CUDA device specification: {device}")
    raise ValueError(f"Unsupported device specification for g2pw: {device}")


def _provider_config_for(device_key: str) -> Tuple[Optional[List[str]], Dict[str, Dict[str, Union[str, int]]]]:
    if device_key == "auto":
        return None, {}
    if device_key == "cpu":
        return ["CPUExecutionProvider"], {}
    if device_key.startswith("cuda:"):
        device_idx = int(device_key.split(":", 1)[1])
        return ["CUDAExecutionProvider", "CPUExecutionProvider"], {
            "CUDAExecutionProvider": {"device_id": device_idx}
        }
    return None, {}


def get_g2pw_poly_predict(device: Optional[Union[str, int]] = None) -> BertPolyPredict:
    device_key = _normalize_device(device)
    with _G2PW_LOCK:
        predictor = _G2PW_PREDICTORS.get(device_key)
        if predictor is None:
            providers, provider_options = _provider_config_for(device_key)
            predictor = BertPolyPredict(
                g2pw_poly_model_path,
                jsonr_file_path,
                json_file_path,
                providers=providers,
                provider_options=provider_options,
            )
            _G2PW_PREDICTORS[device_key] = predictor
    return predictor


