def cjekfd_cleaners(text, sentence, language, text_tokenizers, tokenizer=None):
    if language == "zh":
        return chinese_to_ipa(text, sentence, text_tokenizers["zh"], tokenizer=tokenizer)
    elif language == "ja":
        return japanese_to_ipa(text, text_tokenizers["ja"])
    elif language == "en":
        return english_to_ipa(text, text_tokenizers["en"])
    elif language == "fr":
        return french_to_ipa(text, text_tokenizers["fr"])
    elif language == "ko":
        return korean_to_ipa(text, text_tokenizers["ko"])
    elif language == "de":
        return german_to_ipa(text, text_tokenizers["de"])
    else:
        raise Exception("Unknown language: %s" % language)

