import argparse
import json
import os
import sys
import threading
import time
from concurrent.futures import ThreadPoolExecutor, as_completed
from importlib.resources import files
from itertools import cycle
from typing import Any, Dict, List, Optional, Sequence, Tuple, Union

import soundfile as sf
from datasets.arrow_writer import ArrowWriter
from tqdm import tqdm

from f5_tts.g2p.g2p import PhonemeBpeTokenizer


DEFAULT_SLOW_WARN_S = 20.0
_thread_local = threading.local()


def parse_devices(device_arg: Optional[str]) -> List[Optional[Union[str, int]]]:
    if not device_arg:
        env_value = os.getenv("F5TTS_G2PW_DEVICE")
        if env_value:
            device_arg = env_value
        else:
            return [None]

    devices: List[Optional[Union[str, int]]] = []
    for raw_value in device_arg.split(","):
        token = raw_value.strip()
        if not token:
            continue
        lowered = token.lower()
        if lowered == "cpu":
            devices.append("cpu")
        elif lowered in {"auto", ""}:
            devices.append(None)
        elif lowered.startswith("cuda:"):
            devices.append(token)
        elif token.isdigit():
            devices.append(int(token))
        else:
            raise ValueError(f"Unsupported device specifier: {token}")
    return devices or [None]


def format_slow_warning(elapsed: float, slow_warn_s: float, sample: Dict[str, Any]) -> Optional[str]:
    if slow_warn_s and elapsed > slow_warn_s:
        txt_preview = (sample.get("text", "") or "")[:40].replace("\n", " ")
        lang = sample.get("lang", "auto")
        return f"Warning: slow sample took {elapsed:.1f}s. lang={lang}, text[:40]={txt_preview}"
    return None


def get_tokenizer(device: Optional[Union[str, int]]) -> PhonemeBpeTokenizer:
    storage = getattr(_thread_local, "tokenizers", None)
    if storage is None:
        storage = {}
        _thread_local.tokenizers = storage
    if device not in storage:
        storage[device] = PhonemeBpeTokenizer(enable_cache=True, g2p_device=device)
    return storage[device]


def process_entry(
    index: int,
    sample: Dict[str, Any],
    device: Optional[Union[str, int]],
    slow_warn_s: float,
) -> Tuple[int, str, Dict[str, Any]]:
    started = time.time()
    try:
        tokenizer = get_tokenizer(device)
        audio_path = sample.get("audio_wav_path")
        text = sample.get("text")
        if not text:
            elapsed = time.time() - started
            return index, "skip", {"warning": format_slow_warning(elapsed, slow_warn_s, sample)}

        lang = (sample.get("lang", "auto") or "auto").lower()
        phonemes, _ = tokenizer.tokenize(text=text, sentence="", language=lang)
        phonemes = phonemes.replace("|", " ")

        duration = sample.get("duration")
        if duration is None and audio_path:
            try:
                duration = sf.info(audio_path).duration
            except Exception as exc:
                elapsed = time.time() - started
                return index, "error", {
                    "message": f"Failed to get duration for audio: {audio_path}, error: {exc}",
                    "warning": format_slow_warning(elapsed, slow_warn_s, sample),
                }

        if (duration or 0) < 1:
            elapsed = time.time() - started
            return index, "skip", {"warning": format_slow_warning(elapsed, slow_warn_s, sample)}

        record = {"audio_path": audio_path, "duration": duration, "text": phonemes}
        elapsed = time.time() - started
        return index, "ok", {
            "record": record,
            "duration": duration,
            "vocab_chars": list(phonemes),
            "warning": format_slow_warning(elapsed, slow_warn_s, sample),
        }
    except Exception as exc:
        elapsed = time.time() - started
        return index, "error", {
            "message": f"Failed to process sample, error: {exc}",
            "warning": format_slow_warning(elapsed, slow_warn_s, sample),
        }


def handle_result(
    result: Tuple[int, str, Dict[str, Any]],
    processed_entries: List[Tuple[int, Dict[str, Any], float]],
    all_vocab_set: set,
    stats: Dict[str, int],
) -> None:
    index, status, payload = result
    warning = payload.get("warning") if isinstance(payload, dict) else None
    if warning:
        print(warning)

    if status == "ok":
        all_vocab_set.update(payload["vocab_chars"])
        processed_entries.append((index, payload["record"], payload["duration"]))
    elif status == "error":
        stats["errors"] += 1
        message = payload.get("message", "Failed to process sample.")
        print(message)
    elif status == "skip":
        stats["skipped"] += 1


def parse_args(argv: Sequence[str]) -> argparse.Namespace:
    parser = argparse.ArgumentParser(description="Preprocess singing data into Arrow format.")
    parser.add_argument("training_file", help="Path to the training metadata JSONL file.")
    parser.add_argument("dataset_name", help="Name of the dataset (used for output directory).")
    parser.add_argument(
        "--num-workers",
        type=int,
        default=1,
        help="Number of worker threads to use for preprocessing.",
    )
    parser.add_argument(
        "--g2p-devices",
        type=str,
        default=None,
        help="Comma-separated list of devices for Chinese polyphone model (e.g. '0,1' or 'cpu').",
    )
    parser.add_argument(
        "--slow-warn-s",
        type=float,
        default=DEFAULT_SLOW_WARN_S,
        help="Log a warning if a sample takes longer than this many seconds to process.",
    )
    return parser.parse_args(argv)


def main(args: argparse.Namespace) -> None:
    try:
        devices = parse_devices(args.g2p_devices)
    except ValueError as err:
        print(f"Invalid device specification: {err}")
        sys.exit(1)

    num_workers = args.num_workers if args.num_workers and args.num_workers > 0 else len(devices)
    num_workers = max(1, num_workers)

    with open(args.training_file, "r", encoding="utf-8") as handle:
        lines = [json.loads(it.strip()) for it in handle.readlines() if it.strip()]

    save_root = os.path.abspath(str(files("f5_tts").joinpath("../../")))
    save_dir = os.path.join(save_root, "data", args.dataset_name)
    os.makedirs(save_dir, exist_ok=True)

    print(f"\nPrepare for {args.dataset_name}, will save to {save_dir}\n")

    processed_entries: List[Tuple[int, Dict[str, Any], float]] = []
    all_vocab_set: set = set()
    stats: Dict[str, int] = {"errors": 0, "skipped": 0}

    if num_workers == 1:
        device_cycle = cycle(devices)
        for idx, sample in enumerate(tqdm(lines, desc="Processing", unit="sample")):
            device = next(device_cycle)
            result = process_entry(idx, sample, device, args.slow_warn_s)
            handle_result(result, processed_entries, all_vocab_set, stats)
    else:
        device_cycle = cycle(devices)
        with ThreadPoolExecutor(max_workers=num_workers) as executor:
            futures = []
            for idx, sample in enumerate(lines):
                device = next(device_cycle)
                futures.append(executor.submit(process_entry, idx, sample, device, args.slow_warn_s))

            with tqdm(total=len(futures), desc="Processing", unit="sample") as progress:
                for future in as_completed(futures):
                    result = future.result()
                    handle_result(result, processed_entries, all_vocab_set, stats)
                    progress.update(1)

    processed_entries.sort(key=lambda item: item[0])
    ordered_records = [entry[1] for entry in processed_entries]
    duration_list = [entry[2] for entry in processed_entries]

    print(f"\nSaving to {save_dir} ...")

    with ArrowWriter(path=os.path.join(save_dir, "raw.arrow")) as writer:
        for line in tqdm(ordered_records, desc="Writing to raw.arrow ..."):
            writer.write(line)
        writer.finalize()

    with open(os.path.join(save_dir, "duration.json"), "w", encoding="utf-8") as handle:
        json.dump({"duration": duration_list}, handle, ensure_ascii=False)

    with open(os.path.join(save_dir, "vocab.txt"), "w", encoding="utf-8") as handle:
        for vocab_char in sorted(all_vocab_set):
            handle.write(vocab_char + "\n")

    print(f"\nFor {args.dataset_name}, sample count: {len(ordered_records)}")
    total_hours = (sum(duration_list) / 3600) if duration_list else 0.0
    print(f"For {args.dataset_name}, total {total_hours:.2f} hours")
    if stats["skipped"] or stats["errors"]:
        print(f"Skipped {stats['skipped']} samples, encountered {stats['errors']} errors.")


